import React, { useState, useEffect } from 'react';
import logo from './chrysus.svg';
import './App.css';
import CountdownTimer from "./helpers/countdownTimer";

function App() {
  const [timeLeft, setTimeLeft] = useState(CountdownTimer());

  useEffect(() => {
		const timer = setTimeout(() => {
			setTimeLeft(CountdownTimer());
		}, 1000);
		return () => clearTimeout(timer);
	}, [timeLeft]);

  return (
    <div className="App">
      <header className="App-header">
      <nav>
        <div className='logo'>
          <img src={logo} className="App-logo" alt="logo" />
          <span>Chrysus</span>
        </div>
        <div className='timer'>
          <div className='box'>
            <p>{timeLeft.days}</p>
            <span>Days</span>
          </div>
          <span className='column'>:</span>
          <div className='box'>
            <p>{timeLeft.hours}</p>
            <span>Hours</span>
          </div>
          <span>:</span>
          <div className='box'>
            <p>{timeLeft.minutes}</p>
            <span>Minutes</span>
          </div>
          <span>:</span>
          <div className='box'>
            <p>{timeLeft.seconds}</p>
            <span>Seconds</span>
          </div>
        </div>
      </nav>
      <p className='header-title'>
        COMING SOON
      </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >

          Learn Chrysus
        </a>
        <div className={"text_animation"}>

		</div>
      </header>
    </div>
  );
}

export default App;
