import { useState, useEffect } from 'react';
import logo from './chrysus.svg';
import './App.css';

function App() {
  const [countdown, setCountDown] = useState({
    days: 0,
    minutes: 0,
    hours: 0,
    seconds: 0,
    time_up: ""
  });
  let deadline = null;
  let x = null;

  const count = () =>{
    let now = new Date().getTime;
    let t = deadline - now;
    const dd =Math.floor(t / (1000 * 60 * 60 * 24));
    const hh = Math.floor((t % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
    const mm = Math.floor((t % (1000 * 60 * 60)) / (1000 * 60));
    const ss = Math.floor((t % (1000 * 60)) / 1000);

    const days = dd < 10 ? "0" + dd : dd;
    const hours = hh < 10 ? "0" + hh : hh;
    const minutes = mm < 10 ? "0" + mm : mm;
    const seconds = ss < 10 ? "0" + ss : ss;

    setCountDown({days, minutes, hours, seconds});

    if (t < 0) {
      clearInterval(x);
      setCountDown({
        days: 0,
        minutes: 0,
        hours: 0,
        seconds: 0,
        time_up: "TIME IS UP"
      });
    }
  }

  useEffect(() => {
    deadline = new Date("May 15, 2023 00:00:00").getTime();
    x = setInterval(count, 1000);
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <nav>
          <img src={logo} className="App-logo" alt="logo" />
          <div className='countdown'>
            <div className='box'>
              <p>{countdown.days}</p>
              <span>Days</span>
            </div>
            <div className='box'>
              <p>{countdown.days}</p>
              <span>Days</span>
            </div>
            <div className='box'>
              <p>{countdown.days}</p>
              <span>Days</span>
            </div>
            <div className='box'>
              <p>{countdown.days}</p>
              <span>Days</span>
            </div>
          </div>
        </nav>
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn Chrysus
        </a>
      </header>
    </div>
  );
}

export default App;
